    def __Click_Result(self):
        '''При нажатии на кнопку "Выполнить" срабатывает метод, который проводит расчет значений,
        если они соотвествуют требованиям вещественного числа, если хотя бы одно значение не проходит проверку,
        то атрибуту присваивается значение None и сработает окно с ошибкой'''
        self.__Dc = self.__check_data(self.lineEdit_Dc.text())
        self.__fn = self.__check_data(self.lineEdit_fn.text())
        self.__n = self.__check_data(self.lineEdit_n.text())
        self.__lm = self.__check_data(self.lineEdit_lm.text())
        self.__group = self.__check_group(self.lineEdit_group.text())
        if None in (self.__Dc, self.__fn, self.__n, self.__lm, self.__group):
            error = QMessageBox()
            error.setWindowTitle('Ошибка')
            error.setText('Неверный тип данных')
            error.setIcon(QMessageBox.Warning)
            error.setStandardButtons(QMessageBox.Ok | QMessageBox.Reset)

            error.setDetailedText(
                f'Входные значения должны быть целыми или вещественными числами, неотрицательными, и не равными нулю. '
                f'Группа материала вводится в виде: заглавная буква и число, например "М1" на вкладке Information')

            error.buttonClicked.connect(self.__error_action)

            error.exec_()
        else:
            self.__count()
            self.__render_data()
            self.label_mistake.setText("")

    def __render_data(self):
        '''Округляем полученные значения'''
        self.label_vc_output.setText(f'{round(self.__vc)}')
        self.label_vf_output.setText(f'{round(self.__vf)}')
        self.label_Q_output.setText(f'{round(self.__Q, 2)}')
        self.label_Pc_output.setText(f'{round(self.__Pc, 2)}')
        self.label_Mc_output.setText(f'{round(self.__Mc, 2)}')
        self.label_Ft_output.setText(f'{round(self.__Ft, 2)}')
        self.label_Tc_output.setText(f'{round(self.__Tc, 2)}')

    @staticmethod
    def __check_data(data):
        '''Проверяем введенные значения, чтобы они соответствовали требованиям вещественного числа,
        возвращаем вещественное число для дальнейших расчетов, иначе возвращаем None'''
        if data.replace('.', '').isdigit() and data.count('.') <= 1 and float(data) != 0:
            return float(data)
        return None

        # try:
        #     cur_num = float(data)
        #     if cur_num:
        #         return cur_num
        # except ValueError:
        #     pass
        # return None

    def __check_group(self, data):
        if data in self.__group_of_materials.keys():
            return data
        return None

    def __error_action(self, btn):
        '''При срабатывании поля с ошибкой, если пользователь нажимает на кнопку "Reset",
        то сбрасываем значения во всех полях'''
        if btn.text() == 'Reset':
            self.label_mistake.setText('Значения были сброшены. Введите значения заново')
            self.__Click_Clear()

    def __count(self):
        '''Рассчитываем режимы резания'''
        self.__kc = self.__group_of_materials[self.__group][0] / (
                self.__fn ** self.__group_of_materials[self.__group][1])
        self.__efficiency = 0.95
        self.__vc = self.__Dc * math.pi * self.__n / 1000
        self.__vf = self.__fn * self.__n
        self.__Q = self.__Dc * self.__fn * self.__vc / 4
        self.__Pc = self.__fn * self.__vc * self.__Dc * self.__kc / 240000
        self.__Mc = self.__Pc * 30000 / (math.pi * self.__n)
        self.__Ft = 0.5 * self.__kc * self.__Dc * self.__fn * math.sin(2.44 / 2) / 2
        self.__Tc = self.__lm / self.__vf

    def __Click_Clear(self):
        '''Отчищаем все поля от введенных и полученных значений'''
        for i in [self.lineEdit_Dc, self.lineEdit_fn, self.lineEdit_n, self.lineEdit_lm, self.lineEdit_group,
                  self.label_vc_output, self.label_vf_output, self.label_Q_output, self.label_Mc_output,
                  self.label_Pc_output, self.label_Ft_output, self.label_Tc_output]:
            i.clear()

        self.label_mistake.setText('Значения были сброшены. Введите значения заново')


